generated.css:
*{ box-sizing:border-box;}
*{margin:0;padding:0;}
*:before,*:after{ box-sizing: border-box;}
ul,ol{ list-style:none;}
a{color:inherit;text-decoration:none;}

img{
    max-width:100%;max-height:100%;
}
body{
    background:#eee;
 }

@media(min-width : 500px){
    .globalHeader{
        margin:60px 0 100px;
    }
}
.globalHeader{
margin:20px;
}

.searchForm{
    display: flex;
    justify-content: space-between;
}

.searchForm > input{ 
width:100%;
margin-right:10px;
height:40px;
padding: 0 10px;
border:1px solid #ddd;
border-radius:4px;
}
.searchForm> button{ 
    white-space: nowrap;
    padding: 0 28px;
    border:none;
    background: #1592AD;
    color: #fff;
    border-radius: 4px;
    font-size: 16px;
}

    @media(min-width: 500px){
         .searchForm{
         max-width:400px;
         margin-left: auto;
         margin-right:auto;
        }
    }

.globalMain {
    background:#ddd;
    max-width: 900px;
    margin-left:auto;
    margin-right:auto;
}

.siteList{
    margin:20px;
    flex-wrap: wrap;
    display:flex;
    justify-content: space-between;
 }
 @media(min-width:500px){
    .siteList{
        margin-left:0;
        margin-right:-25px;
        justify-content: flex-start;
    }
}

.siteList > li{
   margin-bottom:10px;

}
@media(min-width:500px){
    .siteList > li {
        margin-right : 25px;
    }
}
.siteList .site{

    width:160px;
    display:flex;
    justify-content: center;
    align-items: center;
    flex-direction:column;
    background: white ;
    border:1px solid #ddd;
    border-radius:4px;
    padding:20px 0;
    /*height:128px;*/
    position:relative;
    cursor: pointer;
}
.siteList .site > .close{
    position: absolute;
    right:10px;
    top:5px;
    display:none;
    cursor:default;
}
.siteList .site:hover > .close{
    display: block;
}

.siteList .site > .logo { 
    width:64px;
    height:64px;
    display:flex;
    justify-content: center;
    align-items:center;
    font-size: 64px;
}
.siteList .site > .link { 
    font-size: 14px;
    margin-top: 4px;


}

.siteList .addButton{
    width:160px;
    display:flex;
    justify-content: center;
    align-items: center;
    flex-direction:column;
    background: white ;
    border:10px solid #ddd;
    border-radius:4px;
    padding:20px 0;
    height:128px;
 }
 .siteList .addButton .icon{
    width:64px;
    height:64px; 
 }
 .siteList .addButton .text{
 font-size: 14px;
 margin-top: 4px;
 }

 .siteList .addButton .icon-wrapper{
width:64px;
height:64px;
display:flex;
justify-content: center;
align-items: center;

 }
